refresh.onmouseenter = function() 
{
  enabled=false
  refresh.textContent='Update now'
}

refresh.onmouseleave = function()
{
  enabled=true
}




function myRefresh(){  
cards=getCardsFromLocalStorage(userID)
  cards.forEach(card => {
    let city = card.name
    const urlWeather = endpoint.getByCityName(city);
    fetchCity(urlWeather)
  })
  cards.length=0
  renderCards(userID, cards);
  getLocation()

}



const fetchCity = (url) => {
  submit.style.display = 'none';
  loader.style.display = 'flex';
  getWeather(url).then(r => {
    loader.style.display = 'none';
    submit.style.display = 'inline-block';
    if (r.cod === '404') {
      throw new Error(r.message)
    }
    const date = new Date();
    const card = {
      ...r,
      id: date.getTime(),
      retrieved: `Updated on ${date.toLocaleString()}`
    }
    cards.push(card)
    console.log(cards)
    console.log(cards.length)
    renderCards(userID,cards)
    const containerHeight = document.querySelector('.cards-container').offsetHeight;
    const cardsHeight = document.querySelector('.cards').offsetHeight;
    if (cardsHeight > containerHeight) {
      showMoreBtn();
    }

  })
  .catch(e => {
      console.log(e);
      alert('Your city was not found, please check the spelling')
  })



}



if (cards.length!==0){
renderCards(userID, cards);
}

let latitude
let longitude

function getLocation() {
  navigator.geolocation.getCurrentPosition(SendLocation)
}

function SendLocation(data)
{

  latitude=data.coords.latitude
  longitude=data.coords.longitude
  console.log('latitude:', latitude)

  const urlCoord = endpointCoord.getByCoord(latitude,longitude);
  fetchCoord(urlCoord)
  console.log(urlCoord);
}

function fetchCoord(url){
  fetch(url)
  .then(function(response){

      return response.json();
  })
  .then(function(result){


      coordResult(result)
  })
  .catch((error)=>{
      console.log('klaida', error)
  })
}

const nav = document.querySelector('.nav')
function coordResult(result){
  console.log(result)
  const { main, weather, name } = result;
  let locCond = weather[0].main
  let locCity = name
  let locTemp = main.temp
  let locDesc = weather[0].description
  const url = './img/rain.jpg'
  console.log(locCond)
  switch(locCond){
    case "Clear":
      nav.style.background = "url('./rain.jpg')";
      // nav.style.background = `url(${url})`;

      // body.style.backgroundSize = 'cover'

      break;

  }

  let currentCity = document.querySelector('.currentCity')
  currentCity.textContent = `${locCity}:`
  let currentTemp = document.querySelector('.currentTemp')
  currentTemp.textContent = `${locTemp} ℃`
  let currentDesc = document.querySelector('.currentDesc')
  currentDesc.textContent = `${locDesc}`

}
